import React from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "weather-icons/css/weather-icons.css";
import Weather from "./components/weatherapp";

// api.openweathermap.org/data/2.5/weather?q=London,uk
const API_KEY = "db0722dc5ad7c43d8271bb13f965e3fb";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: undefined,
      country: undefined,
      icon: undefined,
      main: undefined,
      celsius: undefined,
      temp_max: undefined,
      temp_min: undefined,
      description: "",
      error: false,
    };
    this.getWeatherData();
    this.weathericon = {
      Thunderstorm: "wi-thunderstorm",
      Drizzle: "wi-select",
      Rain: "wi-storm-showers",
      Snow: "wi-snow",
      Atmosphere: "wi-fog",
      Clear: "wi-day-sunny",
      clouds: "wi-day-fog"

    }
  }

  calCelsius(temp){
    let cell = Math.floor(temp - 273.15)
    return cell;
  }
  getWeatherData = async () => {
    const api_call = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=Bangalore,in&appid=${API_KEY}`
    );
    const response = await api_call.json();
    console.log(response);
    this.setState({
      city: response.name,
      country: response.sys.country,
      celsius : this.calCelsius(response.main.temp),
      temp_max : this.calCelsius(response.main.temp_max),
      temp_min : this.calCelsius(response.main.temp_min),
      description : response.weather[0].description,
      icon : this.weathericon.Thunderstorm,
      main : response.weather.main

    });
  };
  render() {
    const {
      city,
      country,
      icon,
      main,
      celsius,
      temp_max,
      temp_min,
      description,
      error,
    } = this.state;
    return (
      <div className="App">
        <Weather city={city} country={country} weathericon={icon} main ={main} celsius={celsius} temp_max={temp_max} temp_min={temp_min} description={description} error={error}/>
      </div>
    );
  }
}

export default App;
